INSERT INTO post (id, title, title_en, content, content_en, published, post_date, created_at, promise_id, source_name, source_link, location) VALUES (1, 'الفيوم استعدت لمليونية النظافة بـ 2000 كاب و تى شيرت و 7 طن أكياس ', '', 'مصطفى البنا
عقد المهندس أحمد على أحمد محافظ الفيوم اجتماعاً حضره أعضاء مجلس الشورى الممثلين عن حزبى الحرية والعدالة والنور وبعض أعضاء الحزبين ومندبين من القوى السياسية والثورية بالمحافظة وذلك للتنسيق فيما بينهم ووضع برنامج العمل لنجاح مليونية النظافة والتجميل.

قال المحافظ أن المحافظة استعدت لبرنامج العمل بحوالى 2000 كاب وتى شرت  للمشاركين و 7 أطنان من أكياس القمامة يتم توزيعها على الأهالى و أعتمد المبالغ المالية لدهانات البلدورات و الأعمدة وشراء أدوات النظافة .وتقرر خلال الاجتماع اعتبار يوم " الجمعة " يوم عمل مضاعف للعاملين وصرف مكأفآت سخية لأجمل قرية وشارع وحى تصل إلى 15 ألف جنية .

وطالب حسن أبو حامد منسق شباب الثورة بالمحافظة بضرورة إدراج أسماء المشاركين فى حملة النظافة من القوى السياسية بلوحة الشرف بالوحدات المحلية لشحذ همم الأهلاى و دفعهم للمشاركة الجادة . وقال الدكتور وجية الشيمى عضو الشعب عن حزب النور لابد من أستغلال خطبة الجمعة  و أئمة المساجد لتوعية الأهالى بالمشاركة و أستخدام مكبرات الصوت بالمساجد فى تجميع الشباب و أعتبار الموضوع قضية كل شاب و مواطن بالمحافظة.', '', 1, '2012-07-26', '2012-07-26 00:30:37', 54, 'الشروق', 'http://www.shorouknews.com/news/view.aspx?cdate=25072012&id=02da8f10-1838-44df-a4ca-9672270d4ac5', 'الفيوم');
