INSERT INTO post (id, title, title_en, content, content_en, published, post_date, created_at, promise_id, source_name, source_link, location) VALUES (3, ' الحملة الشعبية للنظافة يومي الجمعة والسبت بمدينة طنطا', '', 'أصدر محافظ الغربية توجيهات لجميع رؤساء المدن والأحياء ورؤساء الوحدات المحلية وجميع القطاعات بالمحافظة بالمشاركة فى الحملة التى دعا إليها رئيس الجمهورية والتى تحمل شعار (وطن نظيف يساوى مواطن متحضر).

