INSERT INTO post (id, title, title_en, content, content_en, published, post_date, created_at, promise_id, source_name, source_link, location) VALUES (49, 'الرئيس يسقط مديونيات صغار المزارعين بسيناء والمحافظات من 219 مليون جنية', '', '
صدق السيد الدكتور محمد مرسي رئيس الجمهورية علي قرارا باعفاء صغار المزارعيين من مديونيات بنك التنمية والائتمان الزراعي والذين تبدا مديونياتهم من ألف جنيه وحتي 10 الاف جنيه .

يستفيد من هذا القرار حوالي 42 الف مزارع في المحافظات المختلفة .

وتتحمل خزينة الدولة 107 مليون جنيه في هذا الامر فقط .

وكما يستفيد 4000 من مزارعي سيناء أيضاً من القرار .
وستتحمل خزينة الدولة في سيناء مبلغ 112 مليون جنيه ..
ياتي ذلك في محاولة الدولة تخفيف الاعباء عن كاهل صغار المزارعين الذين يعانون منذ فترة بسبب تراكم الديون .', '', 0, '2012-08-16', '2012-08-25 17:57:24', null, 'الصفحة الرسمية للسيد رئيس الجمهورية د.محمد مرسي', 'https://www.facebook.com/Egypt.President.Morsi', 'مصر');
