INSERT INTO post (id, title, title_en, content, content_en, published, post_date, created_at, promise_id, source_name, source_link, location) VALUES (53, 'الرئيس يأمر بسرعة دعم شبكة الكهرباء بمحطتين جديدتين للتخلص من مشكلة الإنقطاعات المتكررة للتيار', '', '
