INSERT INTO post (id, title, title_en, content, content_en, published, post_date, created_at, promise_id, source_name, source_link, location) VALUES (57, 'الرئيس يصدق على قانون لجنة الدستور وتعديل لائحة الجامعات', '', 'الرئيس الدكتور مرسي يصدق على القانون رقم 79 لسنة 2012 بمعايير إنتخاب أعضاء الجمعية التأسيسية لإعداد دستور جديد للبلاد ويصدر قرارا بتعديل بعض موائد اللائحة التنفيذية لقانون تنظيم الجامعات', '', 0, '2012-07-15', '2012-08-25 18:26:41', null, 'الصفحة الرسمية للسيد رئيس الجمهورية د.محمد مرسي', 'https://www.facebook.com/Egypt.President.Morsi', 'مصر');
INSERT INTO post (id, title, title_en, content, content_en, published, post_date, created_at, promise_id, source_name, source_link, location) VALUES (58, 'الرئيس يتابع حادث الاسكندرية على مدار الساعة', '', '
أصدر السيد الدكتور محمد مرسى تعليمات لكل أجهزة الدولة بضرورة التحرك الفوري للتعامل مع حادث انهيار عقار بالإسكندرية ، وما تبعه من انهيارات لعقارات مجاورة ، ويتابع سيادة الرئيس على مدار الساعة تطورات الحادث حيث اتصل بالسيد رئيس الوزراء لتكليفه بتحريك كافة المسئولين لسرعة رفع الأنقاض وإنقاذ الضحايا ، وتقييم الموقف ، وتم إيفاد السيد الوزير الدكتور محمد عطية وزير الحكم المحلى للتواجد مع السيد محافظ الاسكندرية ومدير الأمن وقادة البحرية والمنطقة العسكرية الشمالية للعمل على رفع الأنقاض ونقل الجرحى والمصابين وإيواء المتضررين من خلال غرفة عمليات دائمة كما وجه سيادته بضرورة محاسبة المخالفين للقوانين والمتسببين في الحادث فوراً .', '', 0, '2012-07-15', '2012-08-25 18:28:23', null, 'الصفحة الرسمية للسيد رئيس الجمهورية د.محمد مرسي', 'https://www.facebook.com/Egypt.President.Morsi', '');
INSERT INTO post (id, title, title_en, content, content_en, published, post_date, created_at, promise_id, source_name, source_link, location) VALUES (59, ' رئيس الجمهورية يصدر حزمة من القوانين الخاصة بعلاوة العاملين فى الدولة وزيادة المعاشات وتعديل أحكام قانون تنظيم الجامعات والمنطقة الحرة ببورسعيد', '', '
أصدر السيد الدكتور محمد مرسى رئيس الجمهورية اليوم السبت 14 يوليو 2012 عدداً من القوانين التى من شأنها أن تخفف من عبء المواطنين وتحقق متطلبات الفئات المختلفة وجاءت القوانين كالتالى :
1 - قانون بمنح العاملين بالدولة علاوة خاصة مقدارها
15 % من الأجر الاساسى فى 30/6/2012 أو فى تاريخ التعيين لمن يعين بعد هذا التاريخ بدون حدود ، ولاتعتبر هذه العلاوة جزءاً من الأجر الأساسى للعامل ، ولا تخضع لأية ضرائب أو رسوم وتشمل العلاوة العاملين الدائمين والمؤقتين بمكافآت شاملة .
2 - قانون زيادة المعاشات بنسبة 15 % بحد أدنى 50 جنيهاً ، وبدون حد أقصى ، وتعتبر هذه الزيادة جزءاً من المعاش ، وتسرى فى شأنها جميع أحكامه .
3 - قانون زيادة المعاشات العسكرية بنسبة 15 % بحد أدنى 50 جنيهاً ، وبدون حد أقصى ، كما نص القانون على تعديل بعض أحكام التقاعد والتأمين والمعاشات للقوات المسلحة ، كما هو موضح بنص القانون المرفق .
4 - قانون بزيادة قيمة بدل الجامعة لأعضاء هيئة التدريس والوظائف المعاونة بالجامعات اعتباراً من 1/7/2012 ، كما تم تعديل نص المادة 121 ، 123 ، وأضيفت مادة جديدة 13 مكرر لتكون مناصب الجامعة بدءاً من رئيس القسم ثم عميد الكلية أو المعهد وصولاً لرئيس الجامعة بالانتخاب ، ومنح القانون الحق لأساتذة الجامعات ومراكز البحوث والهيئات المجتمعية التى ينطبق عليها الكادر الوارد فى قانون تنظيم الجامعات والذين كانوا أساتذة متفرغين وعينوا بعد سن السبعين فى وظائف أساتذة غير متفرغين قبل سريان هذا القانون أن يصبحوا أساتذة متفرغين ويسرى عليهم أحكام هذا القانون اعتباراً من تاريخ العمل به .
5 - القانون الخاص بالمنطقة الحرة ببورسعيد تقرر زيادة حصة استيرادية إضافية معفاة من الضرائب الجمركية تقدر بمبلغ 10 مليون جنيه ، لتصبح إجمالى القيمة المعفاة 30 مليون جنيه .
