INSERT INTO post (id, title, title_en, content, content_en, published, post_date, created_at, promise_id, source_name, source_link, location) VALUES (71, 'مدير أمن القاهرة يكرم شرطيين ضبطا عاملا وخطيبته يروجان المواد المخدرة ', '', '<img src="http://productnews.link.net/general/news/27-08-2012/n/aa_2012827145321_s4.JPG" class="float-other-side" />
<p>
كافأ اللواء أسامة الصغير مساعد وزير الداخلية مدير أمن القاهرة، فردى شرطة تمكنا من ضبط عامل وخطيبته حال قيامهما بترويج المواد المخدرة فى أحد المراكز التجارية الشهيرة بوسط القاهرة، بعد أن حاولا الهرب من الشرطيين أثناء التحفظ عليهما</p>
<p>
وكان الطوف الأمنى المكون من كل من أمينى الشرطة أمين حسن إبراهيم، وأحمد  إبراهيم حامد من قوة قسم شرطة الأزبكية يتفقدان الحالة الأمنية بمحل خدمتهما
بشارع 26 يوليو بدائرة القسم، وأثناء ذلك تبلغ لهما من الأهالى بتواجد رجل وسيدة  أمام أحد المراكز التجارية الشهيرة يقومان بترويج المواد المخدرة على المارة
بالمنطقة، فتمكنا بمساعدة الأهالى من ضبطهما وتبين أنهما كل من هشام م.م (27 سنة - عامل) وضبط بحوزته 10 جرامات من مخدر الحشيش و675 جنيها ومطواه وهاتف محمول ودراجة بخارية، وخطيبته (23 سنة طالبة).</p>', '', 1, '2012-08-29', '2012-08-29 10:14:23', 1, 'مصراوي', 'http://www.masrawy.com/news/cases/general/2012/august/27/5300344.aspx', 'القاهرة');
INSERT INTO post (id, title, title_en, content, content_en, published, post_date, created_at, promise_id, source_name, source_link, location) VALUES (72, 'بالصور.. حملة أمنية لإزالة عشوائيات سوق "العاشر" وسط رفض الباعة الجائلين ', '', '<img src="http://gate.ahram.org.eg/Media/News/2012/8/29/2012-634818663241937044-193.jpg" class="float-other-side" />
<p>شنت أجهزة الأمن بالقاهرة حملة أمنية علي سوق موقف العاشر بمدينة نصر، لإزالة التعديات علي الشارع، والإشغالات التي تؤثر علي حركة السير، وقاد الحملة التي استمرت علي مدي 4 ساعات اللواء أسامة الصغير، مدير أمن القاهرة، بمشاركة قوات الأمن المركزي، وقيادات أمنية وشرطية من قسم مدينة نصر أول وثان.
