INSERT INTO post (id, title, title_en, content, content_en, published, post_date, created_at, promise_id, source_name, source_link, location) VALUES (74, 'إغلاق محطة وقود حاول صاحبها بيع 50 طن سولار في السوق السوداء بمرسي علم', '', '<p>أصدر محمود عاصم، محافظ البحر الأحمر، اليوم الخميس، قرارًا بإغلاق نهائي لمحطة وقود بمنطقة برانيس جنوب مدينة مرسي علم لمحاولة صاحبها، بالاشتراك مع سائق سيارة نقل بيع 50 طن سولار من حصة المحطة في السوق السوداء لقرية سياحية بالمنطقة.
</p>
<p>
وقرر المحافظ أيضا إيقاف السيارة التي كانت محملة بالكمية وإيقاف رخصة سائقها لمدة 6 أشهر لاشتراكه في هذه المحاولة.
