INSERT INTO post (id, title, title_en, content, content_en, published, post_date, created_at, promise_id, source_name, source_link, location) VALUES (75, 'ضبط 600لتر سولار قبل بيعه بالسوق السوداء بالفيوم ', '', '<img src="http://productnews.link.net/general/News/25-03-2012/n/solar_L_2012325112119_s4.jpg" class="float-other-side" />
<p>تمكنت مباحث التموين بالفيوم من ضبط 600لتر سولار قبل بيعه بالسوق السوداء، واخطرت النيابة التي تولت التحقيقات.
كان اللواء سعد زغلول مدير أمن الفيوم، قد تلقى إخطاراً من العميد توفيق سعد رئيس مباحث التموين عن ضبط سيارة محملة بـ600لتر من السولار قبل بيعها بالسوق السوداء.</p>
<p>
وبمواجهة  المتهم ويُدعى هاني.س.ع 41سنة سائق ومقيم بالقليوبية، بما أسفر عنه الضبط، اعترف بقيامه بتجميع المواد البترولية المضبوطة لإعادة بيعها بالسوق السوداء، وتحرر عن ذلك محضر برقم 18339لسنة2012جنح مركز شرطة الفيوم، وجاري العرض على النيابة.
<p>', '', 1, '2012-08-26', '2012-08-31 23:03:43', 64, 'مصراوي', 'http://www.masrawy.com/news/cases/general/2012/august/26/5297630.aspx', 'الفيوم');
INSERT INTO post (id, title, title_en, content, content_en, published, post_date, created_at, promise_id, source_name, source_link, location) VALUES (76, 'ضبط 20 طن سولار وبنزين في محطة وقود غير مرخصة بالشرقية ', '', '<p>تمكنت مباحث التموين بالشرقية من ضبط 20 طن سولار وبنزين 80 لدى صاحب محطة وقود غير مرخصة.. تم تحرير المحضر وأُخطرت النيابة العامة للتحقيقات.</p>
<p>
تلقى اللواء محمد كمال جاد مدير أمن الشرقية،  إخطارًا من العميد كامل محمد علي، مدير إدارة التموين بالشرقية،  يُفيد ضبط المدعو محسن عبد السيد 43 سنة، صاحب محطة تموين سيارات بمركز ههيا بدون ترخيص، ضبط وبحوزته (10000 لتر بنزين 80) و(10000  لتر سولار ).
أمر مدير الأمن بالتحفظ على المواد البترولية، وتحرر المحضر رقم 13641 لسنة 2012 جنح ههيا بالواقعة، وتُباشر النيابة التحقيق.</p>', '', 1, '2012-08-28', '2012-08-31 23:08:08', 64, 'مصراوي', 'http://www.masrawy.com/news/cases/general/2012/august/28/5301772.aspx', 'الشرقية');
INSERT INTO post (id, title, title_en, content, content_en, published, post_date, created_at, promise_id, source_name, source_link, location) VALUES (77, 'مدير أمن الفيوم يكرم شرطى مرور كفء', '', '<img src="http://dostor.org/images/stories/hlnkh,-vgfhjudcrgf-cfrh-cfjnh.jpg" class="float-other-side" />
