INSERT INTO post (id, title, title_en, content, content_en, published, post_date, created_at, promise_id, source_name, source_link, location) VALUES (78, 'مدير أمن الفيوم يُكرم مجند تصدى لمحاولة تهريب متهم من السجن', '', '<img src="http://productnews.link.net/general/news/11-08-2012/n/tn_201281123228_s4.jpg" class="float-other-side" />
