INSERT INTO post (id, title, title_en, content, content_en, published, post_date, created_at, promise_id, source_name, source_link, location) VALUES (79, ' الرئيس يدعم الداخلية بسيارات وطائرات مروحية لتنفيذ مهامها', '', '<p>كد الدكتور ياسر على، المتحدث الرسمى باسم رئاسة الجمهورية، على دعم الرئيس محمد مرسى، لكل طلبات وزارة الداخلية للحصول على سيارات مجهزة، أو حتى على طائرات مروحية لتنظيم المرور والعمل الأمنى.
