INSERT INTO post (id, title, title_en, content, content_en, published, post_date, created_at, promise_id, source_name, source_link, location) VALUES (85, 'الصور.. إزالة الإشغالات والباعة الجائلين من الوراق والمنيب', '', '<img src="http://gate.ahram.org.eg/Media/News/2012/8/31/2012-634819976315896144-589.jpg" class="float-other-side"/>
