INSERT INTO post (id, title, title_en, content, content_en, published, post_date, created_at, promise_id, source_name, source_link, location) VALUES (95, ' ضبط 2000 لتر بنزين قبل بيعها بالسوق السوداء في المنيا ', '', '
<p>تمكنت الأجهزة الأمنية بالمنيا، بالتعاون مع مباحث التموين بمركز مغاغة، من ضبط 2000 لتر بنزين  80 بحوزة سائق قبل ترويجها بالسوق السوداء.</p>', '', 1, '2012-09-10', '2012-09-11 11:21:53', 64, 'مصراوي', 'http://www.masrawy.com/news/cases/general/2012/september/10/5340814.aspx', 'المنيا');
INSERT INTO post (id, title, title_en, content, content_en, published, post_date, created_at, promise_id, source_name, source_link, location) VALUES (96, ' ضبط 15 طن سولار وبنزين قبل بيعها في السوق السوداء بالأقصر', '', '<img src="http://www.almasryalyoum.com//sites/default/files/imagecache/highslide_zoom/photo/2012/01/17/228/3709.jpg" class="float-other-side"/>


<p>
تمكن ضباط إدارة شرطة التموين والتجارة الداخلية بمحافظة الأقصر، بالتعاون مع مديرية التموين، من ضبط نحو 15 طن سولار وبنزين بمدينة «أرمنت»، قبل بيعها في السوق السوداء.

وكانت مباحث التموين بالأقصر قد شنت حملات مكثفة على تجار السوق السوداء، وتمكنت من ضبط «النجار. أ. ع»، 36 عامًا، عامل، ومقيم بقرية المحاميد قبلي بمدينة أرمنت، وبحوزته 10 أطنان بنزين وسولار تمت تعبئتها داخل براميل وجراكن كبيرة الحجم.
</p>', '', 1, '2012-09-05', '2012-09-11 11:21:53', 64, 'المصري اليوم', 'http://www.almasryalyoum.com/node/1094696', 'الأقصر');
INSERT INTO post (id, title, title_en, content, content_en, published, post_date, created_at, promise_id, source_name, source_link, location) VALUES (97, ' إحباط محاولة تهريب 4 آلاف لتر سولار مدعمة في البحيرة', '', '<img src="http://www.almasryalyoum.com//sites/default/files/imagecache/highslide_zoom/photo/2012/05/22/115366/albaz91641.jpg" class="float-other-side">

<p>


تمكنت مباحث التموين بمحافظة البحيرة، الجمعة، خلال حملاتها التموينية المكثفة بمراكز المحافظة ومدنها من ضبط 4 آلاف لتر سولار وبنزين مدعم قبل بيعها بالسوق السوداء، وتحرر محضر بالواقعة، وأخطرت النيابة للتحقيق.
</p>', '', 1, '2012-08-10', '2012-09-11 11:26:38', 64, 'المصري اليوم', 'http://www.almasryalyoum.com/node/1042746', 'البحيرة');
INSERT INTO post (id, title, title_en, content, content_en, published, post_date, created_at, promise_id, source_name, source_link, location) VALUES (98, ' إحباط تهريب 54 ألف لتر بنزين إلى الخارج', '', '<img src="http://www.almasryalyoum.com//sites/default/files/imagecache/highslide_zoom/photo/2012/03/30/4886/5008.jpg" class="float-other-side">

<p>


