INSERT INTO post (id, title, title_en, content, content_en, published, post_date, created_at, promise_id, source_name, source_link, location) VALUES (120, 'المشروع منجز جزئيا', '', '<p>هذا المشروع منجز جزئيا  لأنه يطبق فى جميع المحافظات عدا بعض المناطق النائية مترامية الأطراف والتى لا يمكن الفصل فيها ، والمشروع مطبق على نحو 92 % من عدد المخابز ، ويطبق بعدة طرق ، وهى :
</p>
<p>
فى القاهرة والجيزة عن طريق منافذ شركة المصريين للتوزيع والخدمات التى تستلم الخبز من المخابز طبقا للحصص ، وتقوم بالبيع من خلال المنافذ التابعة لها .
فى باقى المحافظات ، أما عن طريق المنافذ الثابتة أو المتحركة عن طريق الوحدات المحلية ، أو عن طريق قيام بعض الجمعيات باستلام الخبز وتوزيعه على المواطنين بموجب كشوف توزيع وحصة محددة للمواطن .
</p>

<a href="http://goo.gl/kDN2w">المصدر: بيان رئاسي</a>', '', 1, '2012-10-07', '2012-10-08 07:22:07', 40, 'بيان من المستشار يحيي حامد - رئاسة الجمهورية ', 'http://goo.gl/kDN2w', 'جميع المحافظات');
INSERT INTO post (id, title, title_en, content, content_en, published, post_date, created_at, promise_id, source_name, source_link, location) VALUES (121, 'دعم وتقوية المخابز الكبيرة المجمعة ', '', '<p>
			ينفذ هذا المشروع على محورين ، هما :ـ</p>
		<p>
			فعلى مستوى المحور الاول تم تنفيذ هذا التكليف فور صدوره حيث قامت الوزارة بالاتى :</p>
		<p>
			المحور الثانى : فى مجال إنشاء المخابز المليونية الجديدة &quot; جارى &quot;</p>
		<p>
			<br />
			<strong>المحور الأول </strong>: دعم المخابز القائمة حاليا كبيرة الإنتاجية التابعة لقطاع الأعمال العام أو المخابز المليونية سواء الآلية منها أو النصف آلية المنشأة بالمحافظات وتدبير الحصص لها وتم انجازه .</p>
		<p>
			<strong>المحور الثانى :</strong> المضى قدما فى إنشاء المخابز المليونية المجمعة بتمويل من منح أو عن طريق المحافظات .</p>
		<p>
			&nbsp;</p>
		<ul>
			<li>
				تركيب وتشغيل 9 خطوط جديدة لإنتاج 300 ألف رغيف يوميا بالطالبية &quot; الهرم &quot; محافظة الجيزة التابع للشركة العامة للمخابز والمخصص لإنتاج خبز بلدى ملدن بسعر خمسة قروش ، وفى انتظار افتتاحه من السيد الدكتور رئيس مجلس الوزراء أول أكتوبر القادم ، مما يضيف طاقة إنتاجية كبيرة من الخبز ذو الجودة العالية لخدمة المواطنين . &nbsp;</li>
			<li>
				زيادة خطوط الإنتاج بمخابز كل من 15 مايو والدقى وإمدادها بحصة الدقيق اللازمة لاستيعاب زيادة الطلب على الخبز فى تلك المنطقتين.</li>
			<li>
				تم زيادة حصة الدقيق لمحافظة الأقصر بكمية 120 طن / شهر لتشغيل خطين جديدين فى المخبز المليونى المنشأ بالمحافظة لتوفير رغيف الخبز وزيادة المعروض من الخبز بالمحافظة .</li>
			<li>
				توفير كامل الحصة لتشغيل المخبز الآلى المليونى بالشيخ زايد بمحافظة الجيزة ، لإنتاج 1,4 مليون رغيف لتوفير الخبز المدعم لمناطق 6 أكتوبر والشيخ زايد ومشروع ابنى بيتك والمناطق المجاورة بإمبابة والوراق وبولاق الدكرور.</li>
			<li>
				توفير كامل الحصة لتشغيل مخبز مليوني بمحافظة قنا لإنتاج الرغيف البلدي المدعم ، فى إطار جهود الوزارة لتوفير الخبز البلدي .</li>
			<li>
				توفير كامل الحصة لمخبز كبير الإنتاجية بمرسى مطروح .</li>
			<li>
				تم إفتتاح مخبز كبير بالعواميه بندر الأقصر فى إطار جهود الوزارة لتوفير الخبز البلدي</li>
		</ul>
		<p>
			<br />
			فى إطار اتفاقية تبادل الديون مع الجانب الايطالى وبعد إنشاء مخبز الشيخ زايد بالجيزة وبدأ العمل فى مطلع هذا العام جارى العمل لإنشاء عدد 2 مخبز آلى عملاق يقع فى محافظة القليوبية ( حى شرق شبرا الخيمة ) وتم وضع حجر الأساس له ، ومخبز اخر فى محـــافظة الجيزة ( منطقة كعبيش ) بهدف توفير الخبز المدعم وتيسير الحصول عليه لكافة المواطنين ، وكذا الإسكندرية .</p>
		<p>
			وامتدادا لهذه المسيرة تطرق الوزارة كافة الأبواب بالتنسيق والتعاون مع وزارتى التعاون الدولى والخارجية لتفعيل أوجه التعاون مع الدول الصديقة المختلفة لمساعدة مصر فى إنشاء مجمعات المخابز المليونية ، ومن هذه الجهات دولة ألمانيا وايطاليا والصين وغيرها من الدول .</p>
		<p>
			تشجيع المحافظات على إنشاء المخابز المليونية أسوة بما تم فى المنيا وقنا والاقصر ، وتوفير الحصص الكاملة من الدقيق لها .<br />
			&nbsp;</p>', '', 1, '2012-10-07', '2012-10-08 07:28:56', 43, 'بيان من المستشار يحيي حامد - رئاسة الجمهورية ', 'http://goo.gl/kDN2w', 'جميع المحافظات');
INSERT INTO post (id, title, title_en, content, content_en, published, post_date, created_at, promise_id, source_name, source_link, location) VALUES (122, 'نسبة الانجاز  نحو 54%', '', '<p>
     بموجب العقد الموقع مع أصحاب المخابز ، يتم منح المخبز الذى لا تحرر ضده أية مخالفات تموينية خلال الشهر حافز جودة قدره ( 5 جنيه ) عن كل جوال ، طبقا لحصته الشهرية التى قام بتشغيلها بالفعل .
<br />
     وخلال الشهر يتم صرف الحافز لعدد  نحو 10465 مخبز من اجمالى عدد المخابز وعددها 19200 مخبز بنسبة نحو 54 % منها ،  يتم صرفها فور إتاحتها من         وزارة المالية .
</p>', '', 1, '2012-10-07', '2012-10-07 00:00:00', 47, 'بيان من المستشار يحيي حامد - رئاسة الجمهورية ', 'http://goo.gl/kDN2w', 'جميع المحافظات');
INSERT INTO post (id, title, title_en, content, content_en, published, post_date, created_at, promise_id, source_name, source_link, location) VALUES (123, 'انجاز جزئي 11%', '', '<p>  بلغت عدد المخابز التى تعمل بالغاز حتى 30/9/2012  2144 مخبز ، تمثل نسبة      11 % من اجمالى المخابز .<br/>
     تقوم الوزارة من جانبها باتخاذ كافة الإجراءات التى تشجع على تحول المخابز التى تعمل بالسولار الى العمل بالغاز ، ويتم التنبيه باستمرار على أصحاب المخابز بإيقاف الحصة        فى حالة عدم التحول الى الغاز .
<br />
     ومساهمة من الوزارة فى التخفيف عن أصحاب المخابز تم مد المهلة المقررة للمخبز   الذى يقع على خط الغاز لتوفيق أوضاعه والتحول الى الغاز .
</p>', '', 1, '2012-10-07', '2012-10-08 07:34:12', 48, 'بيان من المستشار يحيي حامد - رئاسة الجمهورية ', 'http://goo.gl/kDN2w', 'جميع المحافظات');
INSERT INTO post (id, title, title_en, content, content_en, published, post_date, created_at, promise_id, source_name, source_link, location) VALUES (124, 'انجاز جزئى', '', '<p>
			<strong>ينقسم مفتشى التموين الى قسمين ، هما :ـ</strong></p>
		<ul>
			<li>
				العاملون بديوان عام الوزارة ويحصلون على مكافآت وحوافز وبدل جهود من الوزارة ومقابل الضبطية القضائية ، وليس لهم أية طلبات أخرى .</li>
			<li>
				العاملون فى مديريات التموين والمفتشون وموازناتهم تابعة للمحليات&nbsp;</li>
		</ul>
		<p>
			وقد تقدموا بطلبات عديدة مالية قام معالى السيد الوزير بالرد عليها ، وتمثلت فى الاتى :</p>
		<ul>
			<li>
				<p style="margin-left: 40px; ">
					إصدار طابع تموينى يوضع على خدمات التموين يعود عائده على العاملين بالتموين .</p>
			</li>
			<li>
				وذلك يستلزم عدة إجراءات قانونية وتشريعية يمكن دراستها بمعرفة المختصين القانونيين والعرض على السلطة التشريعية لإتخاذ ما يلزم بشأنها .</li>
			<li>
				رفع مكافأة نهاية الخدمة للعاملين بالتموين من شهر إلى شهرين .</li>
			<li>
				وذلك يتطلب الدراسة مع وزارة المالية فى إطار ما تتخذه من إجراءات للإصلاح المالى الشامل لكافة العاملين بالدولة .</li>
			<li>
				بالنسبة لوضع ضوابط للصرف من حصيلة الغرامات للعاملين بالتموين بنسب متساوية .</li>
			<li>
				فقد تم التنبيه على السادة مديرى المديريات تنفيذ ضوابط الصرف على كافة ما يصرف من مزايا مادية بما يحقق العدالة بين جميع العاملين .</li>
			<li>
				صرف بدل الضبطية القضائية للعاملين بالمحافظات بنسبة 100 % أسوة بالوزارة فقد تم مخاطبة السيد اللواء / وزير التنمية المحلية للنظر فى إمكانية تدبيرها من حصيلة صندوق المحافظات .</li>
			<li>
				زيادة حافز الإنتاج
				<ul>
					<li>
						فقد قام معالى الوزير بإصدار تعليمات بإتخاذ إجراءات تدبير صرف مبلغ 5.3 مليون جنيه سنوياً تخصص للعاملين بالمديريات إعتباراً من 1/10/2012 ، وذلك فى حدود 150 جنيهاً لكل مفتش من خارج الموازنة العامة للدولة .</li>
				</ul>
			</li>
			<li>
				صرف بدل طبيعة العمل أو بدل المخاطر
				<ul>
					<li>
						فيتم صرف بدل عدوى للعاملين ذوى الإختصاص ، أما بالنسبة لصرف بدل طبيعة عمل فهى محددة بقرار السيد رئيس مجلس الوزراء رقم 525 لسنة 2009 والجهاز المركزى للمحاسبات للفئات على سبيل الحصر .</li>
				</ul>
			</li>
			<li>
				تحديد العلاوة الدورية ليكون 7%
				<ul>
					<li>
						فهذا أمر يدخل فى نطاق قانون نظام العاملين بالدولة وسياسة الدولة فى هذا الشأن يتجه إلى تحقيق العدالة الإجتماعية ورفع مستوى الأجور فى ظل منظومة إصلاح شامل .</li>
				</ul>
			</li>
		</ul>
		<p>
			هـذا وعلـى الرغـم مـن أن مديريات التموين تتبع المحافظات إلا ان الوزارة قامت بإتخاذ إجراءات تدبير موارد لزيادة حافز الإنتاج للمديريات ودعم العاملين بها تحت مسئوليتها وتتخذ ما يمكنها من إجراءات لتلبية طلباتها .<br />
			&nbsp;</p>', '', 1, '2012-10-07', '2012-10-08 07:39:37', 50, 'بيان من المستشار يحيي حامد - رئاسة الجمهورية ', 'http://goo.gl/kDN2w', 'جميع المحافظات');
